(()=>{const e=document.querySelector("#mobile-menu"),n=document.querySelector("#open-menu"),o=document.querySelector("#close-menu"),s=document.querySelector("#change-header-nav");n.addEventListener("click",t),o.addEventListener("click",t);function t(){document.body.classList.toggle("modal-open"),e.classList.toggle("is-open"),n.classList.toggle("is-open"),s.classList.toggle("is-open"),o.classList.toggle("is-open")}window.matchMedia("(min-width: 768px)").addEventListener("change",c=>{c.matches&&(e.classList.remove("is-open"),document.body.classList.remove("modal-open"))})})();
